function populateTable(tableId, data, columns) {
    const tableBody = document.getElementById(tableId).querySelector('tbody');
    if (!tableBody) return;
    tableBody.innerHTML = ''; // Clear existing rows
    data.forEach(rowData => {
        const row = tableBody.insertRow();
        columns.forEach(column => {
            const cell = row.insertCell();
            cell.textContent = rowData[column.key];
        });
    });
}

document.addEventListener('DOMContentLoaded', function () {
    // Poblar tablas
    populateTable('actores-table', actoresData, [
        { key: 'actor', label: 'Actor' },
        { key: 'desc', label: 'Descripción Breve' },
        { key: 'rol', label: 'Rol Principal' }
    ]);
    populateTable('enfoques-table', enfoquesData, [
        { key: 'caracteristica', label: 'Característica' },
        { key: 'regulatorio', label: 'Enfoque Regulatorio' },
        { key: 'mercado', label: 'Enfoque de Mercado' }
    ]);
    populateTable('psd2-table', psd2Data, [
        { key: 'componente', label: 'Componente de PSD2' },
        { key: 'impactoBancos', label: 'Impacto en Bancos (ASPSP)' },
        { key: 'impactoTpps', label: 'Impacto en TPPs' },
        { key: 'impactoConsumidores', label: 'Impacto en Consumidores' }
    ]);

    // Gráfico de Adopción Global
    const globalAdoptionCtx = document.getElementById('globalAdoptionChart')?.getContext('2d');
    if (globalAdoptionCtx) {
        new Chart(globalAdoptionCtx, {
            type: 'bar',
            data: {
                labels: processLabels(['Europa', 'Asia-Pacífico', 'América Latina', 'América del Norte', 'África']),
                datasets: [{
                    label: 'Nivel de Madurez/Adopción (Ilustrativo)',
                    data: [9, 8, 7, 6, 4], // Datos ilustrativos
                    backgroundColor: ['#0077B6', '#00B4D8', '#90E0EF', '#00A1C9', '#6CBEE0'],
                    borderColor: '#005A8D',
                    borderWidth: 1
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                scales: {
                    y: {
                        beginAtZero: true,
                        max: 10,
                        title: { display: true, text: 'Nivel (1-10)' }
                    },
                    x: {
                        ticks: { autoSkip: false }
                    }
                },
                plugins: {
                    legend: { display: true, position: 'top' },
                    tooltip: commonTooltipConfig
                }
            }
        });
    }

    // Gráfico de Crecimiento de Usuarios
    const userGrowthCtx = document.getElementById('userGrowthChart')?.getContext('2d');
    if (userGrowthCtx) {
        const userGrowthData = {
            labels: ['2025', '2026', '2027', '2028', '2029'],
            datasets: [{
                label: 'Usuarios Globales de Open Banking (Millones)',
                data: [183, 298.5, 414, 529.5, 645], // Dato del informe: 183M en 2025 a 645M en 2029 (interpolado)
                fill: true,
                backgroundColor: 'rgba(0, 180, 216, 0.2)',
                borderColor: '#00B4D8',
                tension: 0.1
            }]
        };
        new Chart(userGrowthCtx, {
            type: 'line',
            data: userGrowthData,
            options: {
                responsive: true,
                maintainAspectRatio: false,
                scales: {
                    y: {
                        beginAtZero: false,
                        title: { display: true, text: 'Millones de Usuarios' }
                    }
                },
                plugins: {
                    legend: { display: true, position: 'top' },
                    tooltip: commonTooltipConfig
                }
            }
        });
    }

    // Smooth scroll para la navegación
    document.querySelectorAll('nav a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();
            const targetId = this.getAttribute('href');
            const targetElement = document.querySelector(targetId);
            if (targetElement) {
                // Quitar clase 'active' de todos los items
                document.querySelectorAll('nav a.nav-item').forEach(item => item.classList.remove('active'));
                // Añadir clase 'active' al item clickeado
                this.classList.add('active');

                targetElement.scrollIntoView({
                    behavior: 'smooth'
                });
            }
        });
    });

    // Activar nav item según scroll
    const sections = document.querySelectorAll('section[id]');
    const navItems = document.querySelectorAll('nav a.nav-item');

    window.addEventListener('scroll', () => {
        let current = '';
        sections.forEach(section => {
            const sectionTop = section.offsetTop;
            const sectionHeight = section.clientHeight;
            if (pageYOffset >= (sectionTop - sectionHeight / 3 - 70)) { // 70px para el nav sticky
                current = section.getAttribute('id');
            }
        });

        navItems.forEach(item => {
            item.classList.remove('active');
            if (item.getAttribute('href').substring(1) === current) {
                item.classList.add('active');
            }
        });
         // Si no hay sección actual (ej. al inicio o final), desmarcar todos
        if (!current && pageYOffset < sections[0].offsetTop) {
             navItems.forEach(item => item.classList.remove('active'));
        }
    });


});
